import math
from numpy.lib.scimath import sqrt as csqrt
import decimal


class PiDay:
	pi_str = ""

	def __init__(self, limit):
		self.limit = limit

	# BY HARISH
	@staticmethod
	def calcPi(limit):

	  q, r, t, k, n, l = 1, 0, 1, 1, 3, 3

	  decimal = limit
	  counter = 0

	  while counter != decimal + 1:
	    if 4 * q + r - t < n * t:

	      yield n
	      
	      if counter == 0:
	        yield '.'
	      
	      if decimal == counter:
	        print('')
	        break
	        
	      counter += 1
	      nr = 10 * (r - n * t)
	      n = ((10 * (3 * q + r)) // t) - 10 * n
	      q *= 10
	      r = nr

	    else:
	      nr = (2 * q + r) * l
	      nn = (q * (7 * k) + 2 + (r * l)) // (t * l)

	      q *= k
	      t *= l
	      l += 2
	      k += 1
	      n = nn
	      r = nr


	# BY ARIN
	@staticmethod
	def factorial(n):
	    factorials = [1]
	    for i in range(1, n + 1):
	        factorials.append(factorials[i - 1] * i)
	    return factorials


	# BY ARIN
	def compute_e(self):
		n = self.limit
		decimal.getcontext().prec = n + 1
		e = 2
		factorials = self.factorial(2 * n + 1)
		for i in range(1, n + 1):
			counter = 2 * i + 2
			denominator = factorials[2 * i + 1]
			e += decimal.Decimal(counter / denominator)
		print(str(e))


	# BY HARISH
	def get_pi(self):
	  pi_digits = self.calcPi(self.limit)

	  i = 0
	  
	  for d in pi_digits:
	  	print(d, end="")

	  	i += 1
	  	if i == 60:
	  		print("")
	  		i = 0

	# BY ARIN
	def get_relation_e(self):
		e = float(2.)
		pi = float(3.)
		print('')
		print("First Relation Between Pi and e")
		print(round(e**pi-pi))
		print("(e^pi)-pi = 20 [round off]")
		i = csqrt(-1)
		exp = pi*i
		use = (e**exp)+1
		print("")
		print("Second Relation Between Pi and e")
		print(round(use))
		print("e^(pi*i) +1 = 0")
